INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Définir son chez-soi', 'Identifiez ce qui crée votre sentiment d''appartenance', 'morning', 'notebook', '{"objective": "Reconnecter avec votre notion de ''chez-soi''", "instructions": ["Réfléchissez : où vous sentez-vous vraiment chez vous ?", "Ce peut être un lieu, mais aussi des sensations, des personnes", "Décrivez précisément ce qui crée ce sentiment", "Identifiez les éléments reproductibles ailleurs", "Listez 3 actions pour créer ce sentiment aujourd''hui"], "duration": "20 min", "material": "Carnet", "why_helps": "Identifier les composantes du ''chez-soi'' permet de les recréer activement."}', '{"deracine"}', 1, 45, false),
('Marche pieds nus', 'Reconnectez-vous physiquement à la terre', 'morning', 'explanatory', '{"objective": "Créer un ancrage physique et symbolique", "instructions": ["Trouvez un espace sûr (jardin, parc, plage)", "Retirez chaussures et chaussettes", "Marchez lentement pendant 2 minutes", "Ressentez le contact avec le sol", "Imaginez des racines qui poussent de vos pieds", "Respirez profondément cette connexion"], "duration": "10 min", "material": "Espace naturel sûr", "why_helps": "Le contact direct avec la terre active des mécanismes d''ancrage profonds."}', '{"deracine"}', 1, 60, false),
('Carte du moi stable', 'Visualisez vos fondations personnelles', 'evening', 'notebook', '{"objective": "Identifier vos ressources internes stables", "instructions": ["Dessinez un cercle au centre (vous)", "Autour, placez vos valeurs fondamentales", "Ajoutez vos talents et qualités durables", "Incluez vos relations solides", "Connectez ces éléments par des lignes", "Observez cette carte de votre stabilité intérieure"], "duration": "25 min", "material": "Papier, crayons colorés", "why_helps": "Visualiser ses ancrages internes renforce le sentiment de continuité identitaire."}', '{"deracine"}', 3, 90, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Planning aléatoire', 'Acceptez l''imprévisibilité dans votre journée', 'morning', 'notebook', '{"objective": "Développer votre flexibilité et réduire l''anxiété de contrôle", "instructions": ["Listez 6 activités que vous pourriez faire aujourd''hui", "Numérotez-les de 1 à 6", "Lancez un dé pour choisir votre première activité", "Fixez un créneau, puis relancez le dé", "Suivez cet ordre sans le modifier", "Observez vos résistances et votre adaptation"], "duration": "15 min + journée", "material": "Dé, carnet", "why_helps": "S''exercer au lâcher-prise dans un cadre sécurisé développe la tolérance à l''incertitude."}', '{"controlant"}', 1, 45, false),
('Cuisine sans recette', 'Créez sans plan prédéfini', 'afternoon', 'explanatory', '{"objective": "Expérimenter la créativité spontanée", "instructions": ["Ouvrez votre frigo et vos placards", "Choisissez 4-5 ingrédients au hasard", "Commencez à cuisiner sans recette ni plan", "Goûtez et ajustez au fur et à mesure", "Acceptez que le résultat soit imparfait", "Savourez votre création unique"], "duration": "45 min", "material": "Ingrédients disponibles", "why_helps": "La création sans plan développe la confiance dans l''adaptation et l''improvisation."}', '{"controlant"}', 1, 60, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Bulle sensorielle', 'Créez votre cocon de réconfort', 'morning', 'explanatory', '{"objective": "Créer un environnement apaisant pour vos sens", "instructions": ["Choisissez un espace calme", "Tamisez la lumière (bougies, lampe douce)", "Ajoutez un son apaisant (nature, musique douce)", "Entourez-vous de textures douces", "Restez dans cette bulle 15 minutes", "Observez l''apaisement de vos sens"], "duration": "20 min", "material": "Bougies, musique, coussins", "why_helps": "Un environnement sensoriel contrôlé permet de réguler la sur-stimulation."}', '{"hypersensible"}', 1, 45, false),
('Dessiner ses émotions', 'Expression créative de votre ressenti', 'afternoon', 'explanatory', '{"objective": "Extérioriser les émotions par l''art", "instructions": ["Prenez des crayons de couleur", "Choisissez l''émotion la plus forte du jour", "Dessinez cette émotion sans réfléchir", "Utilisez couleurs, formes, mouvements", "Ne jugez pas votre création", "Observez ce que cela vous fait ressentir"], "duration": "15 min", "material": "Papier, crayons de couleur", "why_helps": "L''expression artistique permet de traiter les émotions différemment du mental."}', '{"hypersensible"}', 1, 60, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Journal sans filtre', 'Libérez votre expression authentique', 'evening', 'notebook', '{"objective": "Développer l''expression émotionnelle libre", "instructions": ["Ouvrez un carnet dédié", "Écrivez pendant 10 minutes sans vous arrêter", "Ne censurez rien, laissez tout sortir", "Ne vous relisez pas immédiatement", "Si vous bloquez, écrivez ''je ne sais pas quoi écrire''", "Fermez le carnet et respirez"], "duration": "10 min", "material": "Carnet privé", "why_helps": "L''écriture libre permet de débloquer les émotions refoulées en sécurité."}', '{"refoule"}', 1, 30, false),
('Musique triste', 'Autorisez-vous à ressentir', 'afternoon', 'audio', '{"objective": "Reconnecter avec vos émotions profondes", "instructions": ["Choisissez une musique qui vous émeut", "Asseyez-vous confortablement", "Écoutez en fermant les yeux", "Laissez venir toutes les émotions", "Ne résistez pas si des larmes montent", "Accueillez ce que vous ressentez"], "duration": "15 min", "material": "Musique émouvante", "why_helps": "La musique peut débloquer des émotions que nous n''osons pas exprimer.", "theme": "emotional-music"}', '{"refoule"}', 1, 60, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Crier dans un coussin', 'Libération sécurisée de la tension', 'afternoon', 'explanatory', '{"objective": "Évacuer la pression sans dommage", "instructions": ["Prenez un gros coussin ou oreiller", "Trouvez un espace privé", "Criez dans le coussin aussi fort que nécessaire", "Laissez sortir toute la frustration", "Continuez jusqu''à sentir un soulagement", "Respirez profondément après"], "duration": "5-10 min", "material": "Coussin épais", "why_helps": "Exprimer physiquement la colère permet de la libérer sans nuire."}', '{"volcan"}', 1, 45, false),
('Course défoulement', 'Transformer l''énergie explosive en mouvement', 'morning', 'explanatory', '{"objective": "Canaliser l''énergie intense par le sport", "instructions": ["Sortez courir ou faites du sport intensif", "Concentrez-vous sur l''effort physique", "Imaginez que vous évacuez votre colère par les pieds", "Continuez jusqu''à essoufflement", "Terminez par des étirements", "Observez l''apaisement"], "duration": "20-30 min", "material": "Tenue de sport", "why_helps": "L''exercice intense permet de métaboliser les hormones de stress."}', '{"volcan"}', 1, 60, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Visualisation lieu apaisant', 'Refuge mental universel', 'afternoon', 'audio', '{"objective": "Créer un espace mental de paix", "instructions": ["Fermez les yeux et respirez profondément", "Imaginez un lieu qui vous apaise totalement", "Visualisez tous les détails", "Ressentez la paix de ce lieu", "Ancrez cette sensation", "Revenez-y mentalement quand nécessaire"], "duration": "8 min", "material": "Aucun", "why_helps": "La visualisation active les mêmes zones cérébrales que l''expérience réelle.", "theme": "peaceful-place"}', '{"anxieux", "fatigue", "deracine", "controlant", "hypersensible", "refoule", "volcan"}', 1, 365, false),
('Douche consciente', 'Moment de présence par l''eau', 'evening', 'explanatory', '{"objective": "Utiliser l''eau comme ancrage dans le présent", "instructions": ["Prenez une douche en pleine conscience", "Sentez l''eau sur votre peau", "Concentrez-vous sur les sensations", "Respirez la vapeur", "Laissez l''eau emporter les tensions du jour"], "duration": "10 min", "material": "Douche", "why_helps": "L''attention aux sensations physiques ancre dans le moment présent."}', '{"anxieux", "fatigue", "deracine", "controlant", "hypersensible", "refoule", "volcan"}', 1, 365, false),
('Verre d''eau lent', 'Mindfulness par l''hydratation', 'morning', 'explanatory', '{"objective": "Pratiquer la pleine conscience avec un geste simple", "instructions": ["Prenez un verre d''eau", "Buvez très lentement", "Concentrez-vous sur chaque gorgée", "Ressentez l''eau dans votre bouche", "Sentez-la descendre dans votre corps", "Terminez en respirant profondément"], "duration": "5 min", "material": "Verre d''eau", "why_helps": "La pleine conscience sur un geste simple développe l''attention au présent."}', '{"anxieux", "fatigue", "deracine", "controlant", "hypersensible", "refoule", "volcan"}', 1, 365, false);
