INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Écrire 3 peurs de la semaine', 'Identifiez et extériorisez vos peurs pour mieux les comprendre', 'morning', 'notebook', '{"objective": "Prendre conscience de vos peurs pour les démystifier", "instructions": ["Installez-vous dans un endroit calme", "Réfléchissez aux moments où vous avez ressenti de la peur cette semaine", "Écrivez 3 situations qui vous ont fait peur", "Pour chaque peur, notez ce qui vous inquiétait exactement", "Demandez-vous si cette peur était fondée"], "duration": "15 min", "material": "Carnet et stylo", "why_helps": "L''écriture permet d''extérioriser les peurs et de les rationaliser, réduisant leur emprise émotionnelle."}', '{"anxieux"}', 1, 30, false),
('Respiration en 4 temps', 'Technique de respiration pour calmer immédiatement l''anxiété', 'afternoon', 'explanatory', '{"objective": "Réguler votre système nerveux et retrouver le calme", "instructions": ["Asseyez-vous confortablement, dos droit", "Inspirez par le nez en comptant jusqu''à 4", "Retenez votre souffle en comptant jusqu''à 4", "Expirez par la bouche en comptant jusqu''à 4", "Pausez en comptant jusqu''à 4", "Répétez ce cycle 8 fois"], "duration": "10 min", "material": "Aucun", "why_helps": "Cette respiration équilibrée active le système nerveux parasympathique, responsable de la détente."}', '{"anxieux"}', 1, 60, false),
('Boîte à calme virtuelle', 'Créez votre arsenal personnel d''outils apaisants', 'evening', 'notebook', '{"objective": "Constituer une trousse de secours émotionnelle", "instructions": ["Listez 10 choses qui vous apaisent immédiatement", "Classez-les par type : mentales, physiques, sensorielles", "Notez comment accéder à chacune rapidement", "Créez un raccourci mental vers cette boîte", "Testez 3 éléments de votre liste aujourd''hui"], "duration": "20 min", "material": "Carnet", "why_helps": "Avoir des outils d''apaisement identifiés et accessibles réduit l''anxiété anticipatoire."}', '{"anxieux"}', 3, 90, false),
('Identifier un déclencheur', 'Analysez votre gestion face à un déclencheur d''anxiété', 'morning', 'notebook', '{"objective": "Comprendre vos mécanismes de gestion du stress", "instructions": ["Identifiez une situation récente qui a déclenché votre anxiété", "Décrivez précisément ce qui s''est passé", "Notez vos sensations physiques du moment", "Listez les stratégies que vous avez utilisées", "Évaluez l''efficacité de chaque stratégie", "Identifiez ce que vous pourriez améliorer"], "duration": "15 min", "material": "Carnet", "why_helps": "L''analyse rétrospective développe votre capacité d''auto-observation et d''adaptation."}', '{"anxieux"}', 5, 120, false),
('Visualisation lieu sûr', 'Créez un refuge mental pour vous apaiser', 'afternoon', 'audio', '{"objective": "Développer un espace mental de sécurité", "instructions": ["Fermez les yeux et respirez profondément", "Imaginez un lieu où vous vous sentez parfaitement en sécurité", "Visualisez les détails : couleurs, sons, odeurs", "Ressentez la paix et la sérénité de ce lieu", "Ancrez cette sensation dans votre corps", "Répétez ce processus pour mémoriser ce refuge"], "duration": "12 min", "material": "Aucun", "why_helps": "La visualisation active les mêmes zones cérébrales que l''expérience réelle, créant un apaisement concret.", "theme": "safe-place"}', '{"anxieux"}', 7, 150, false),
('Musique apaisante', 'Utilisez la musique comme thérapie anti-stress', 'evening', 'audio', '{"objective": "Réguler vos émotions par la musique", "instructions": ["Choisissez 3 morceaux qui vous apaisent profondément", "Écoutez-les dans un endroit confortable", "Concentrez-vous uniquement sur la musique", "Observez l''effet sur votre respiration et tension", "Créez une playlist ''secours anxiété''"], "duration": "15 min", "material": "Écouteurs", "why_helps": "La musique influence directement le système limbique, centre des émotions.", "theme": "calming-music"}', '{"anxieux"}', 10, 180, false),
('Rangement thérapeutique', 'Apaisez votre mental en organisant votre espace', 'afternoon', 'explanatory', '{"objective": "Créer de l''ordre extérieur pour calmer l''agitation intérieure", "instructions": ["Choisissez un petit espace à ranger (bureau, tiroir)", "Videz complètement cet espace", "Triez les objets : garder, jeter, déplacer", "Nettoyez l''espace vide", "Replacez les objets de manière organisée", "Observez la sensation de calme qui en découle"], "duration": "30 min", "material": "Chiffon, éventuellement boîtes", "why_helps": "L''ordre extérieur apaise le mental anxieux en créant un sentiment de contrôle et de prévisibilité."}', '{"anxieux"}', 12, 200, false),
('Lettre au moi stressé', 'Dialogue bienveillant entre vos deux facettes', 'morning', 'notebook', '{"objective": "Développer l''auto-compassion et la perspective", "instructions": ["Identifiez une situation actuelle qui vous stresse", "Écrivez en tant que ''vous calme'' à ''vous stressé''", "Utilisez un ton bienveillant et compréhensif", "Donnez des conseils pratiques et réalistes", "Terminez par des encouragements", "Relisez cette lettre quand l''anxiété monte"], "duration": "20 min", "material": "Carnet", "why_helps": "Cette technique développe l''observateur bienveillant intérieur, essentiel pour gérer l''anxiété."}', '{"anxieux"}', 15, 250, false),
('Pause écran 30 minutes', 'Libérez votre mental de la stimulation numérique', 'evening', 'explanatory', '{"objective": "Réduire la sur-stimulation anxiogène", "instructions": ["Éteignez tous vos écrans (téléphone, TV, ordinateur)", "Informez votre entourage de cette pause", "Choisissez une activité analogique (lecture, dessin, marche)", "Observez vos réflexes de vérification des écrans", "Notez les pensées qui émergent dans ce silence", "Appréciez cette déconnexion"], "duration": "30 min", "material": "Activité de substitution", "why_helps": "La réduction des stimuli numériques diminue l''activation du système nerveux sympathique."}', '{"anxieux"}', 18, 300, false),
('Rituel du soir anti-stress', 'Créez une routine apaisante pour bien finir la journée', 'evening', 'explanatory', '{"objective": "Préparer un sommeil serein", "instructions": ["Définissez 5 étapes apaisantes (ex: tisane, lecture, respiration)", "Effectuez ces étapes dans le même ordre chaque soir", "Créez un environnement propice (lumière douce, silence)", "Éliminez les sujets stressants 1h avant", "Notez l''effet sur votre sommeil après une semaine"], "duration": "45 min", "material": "Éléments de votre rituel", "why_helps": "Un rituel prévisible rassure le mental anxieux et prépare la détente."}', '{"anxieux"}', 20, 320, false),
('Marche lente consciente', 'Mouvement méditatif pour calmer l''agitation', 'morning', 'explanatory', '{"objective": "Apaiser le mental par le mouvement lent", "instructions": ["Sortez marcher très lentement pendant 5 minutes", "Concentrez-vous sur chaque pas", "Respirez en rythme avec vos pas", "Si l''anxiété monte, ralentissez encore", "Observez comment la lenteur apaise votre mental"], "duration": "5 min", "material": "Espace pour marcher", "why_helps": "Le mouvement lent active le système nerveux parasympathique et ancre dans le présent."}', '{"anxieux"}', 22, 340, false),
('Tisane en silence', 'Rituel d''apaisement par les sens', 'afternoon', 'explanatory', '{"objective": "Créer un moment de paix sensorielle", "instructions": ["Préparez une tisane apaisante (camomille, verveine)", "Asseyez-vous confortablement", "Buvez lentement, en savourant chaque gorgée", "Concentrez-vous sur la chaleur, le goût, l''arôme", "Laissez cette chaleur détendre votre corps"], "duration": "15 min", "material": "Tisane apaisante", "why_helps": "L''attention sensorielle interrompt le cycle des pensées anxieuses."}', '{"anxieux"}', 25, 365, false),
('Écoute du souffle', 'Méditation simple sur la respiration naturelle', 'morning', 'audio', '{"objective": "Développer l''attention à la respiration", "instructions": ["Asseyez-vous confortablement", "Fermez les yeux", "Écoutez le son de votre respiration naturelle", "Ne modifiez rien, juste observez", "Quand l''esprit divague, revenez au souffle"], "duration": "3 min", "material": "Aucun", "why_helps": "L''attention au souffle est un ancrage immédiat contre l''anxiété.", "theme": "breath-awareness"}', '{"anxieux"}', 28, 365, false),
('Dessiner son anxiété', 'Expression créative de l''émotion', 'evening', 'explanatory', '{"objective": "Extérioriser l''anxiété par l''art", "instructions": ["Prenez du papier et des crayons", "Dessinez votre anxiété sans réfléchir", "Utilisez formes, couleurs, symboles", "Ne jugez pas votre création", "Observez comment vous vous sentez après"], "duration": "20 min", "material": "Papier, crayons", "why_helps": "L''expression artistique permet de traiter les émotions différemment du mental."}', '{"anxieux"}', 30, 365, false),
('Scan corporel guidé', 'Détente progressive de tout le corps', 'evening', 'audio', '{"objective": "Relâcher les tensions physiques liées à l''anxiété", "instructions": ["Allongez-vous confortablement", "Suivez le guide audio", "Détendez chaque partie du corps progressivement", "Observez les zones de tension", "Terminez par une relaxation globale"], "duration": "18 min", "material": "Lieu calme pour s''allonger", "why_helps": "La détente physique induit automatiquement une détente mentale.", "theme": "body-scan"}', '{"anxieux"}', 35, 365, false),
('Tri pensées utiles/inutiles', 'Exercice cognitif de clarification mentale', 'afternoon', 'notebook', '{"objective": "Développer le discernement mental", "instructions": ["Listez les pensées qui vous occupent", "Créez deux colonnes : Utiles / Inutiles", "Classez chaque pensée", "Pour les utiles : définissez une action", "Pour les inutiles : pratiquez le lâcher-prise"], "duration": "20 min", "material": "Carnet", "why_helps": "Trier ses pensées développe la capacité de discernement et réduit la rumination."}', '{"anxieux"}', 40, 365, false),
('Solutions aux inquiétudes', 'Approche proactive des préoccupations', 'morning', 'notebook', '{"objective": "Transformer l''inquiétude en action", "instructions": ["Identifiez vos 3 principales inquiétudes actuelles", "Pour chacune, trouvez au moins 1 solution concrète", "Définissez la première étape réalisable", "Si aucune solution : acceptez ce qui ne dépend pas de vous", "Planifiez l''action pour aujourd''hui"], "duration": "25 min", "material": "Carnet", "why_helps": "Passer de l''inquiétude à l''action réduit l''anxiété et développe l''efficacité personnelle."}', '{"anxieux"}', 45, 365, false),
('Moments de sécurité', 'Inventaire des expériences rassurantes', 'evening', 'notebook', '{"objective": "Renforcer les ressources internes de sécurité", "instructions": ["Listez 10 situations où vous vous êtes senti(e) en sécurité", "Décrivez ce qui créait cette sécurité", "Identifiez les éléments reproductibles", "Choisissez 3 éléments à intégrer dans votre quotidien"], "duration": "20 min", "material": "Carnet", "why_helps": "Se rappeler des expériences de sécurité renforce la confiance en sa capacité à être bien."}', '{"anxieux"}', 50, 365, false),
('Objectifs réalistes', 'Planification bienveillante de la journée', 'morning', 'notebook', '{"objective": "Éviter la pression excessive", "instructions": ["Listez ce que vous aimeriez accomplir", "Réduisez cette liste de moitié", "Définissez des objectifs SMART", "Ajoutez du temps de marge", "Célébrez chaque accomplissement"], "duration": "15 min", "material": "Agenda", "why_helps": "Des objectifs réalistes évitent le stress de performance et renforcent l''estime de soi."}', '{"anxieux"}', 55, 365, false),
('Anticipation excessive', 'Reconnaître et réguler l''anticipation négative', 'afternoon', 'notebook', '{"objective": "Gérer la tendance à anticiper le pire", "instructions": ["Identifiez une situation que vous anticipez négativement", "Listez tous les scénarios que vous imaginez", "Évaluez la probabilité réelle de chacun", "Imaginez 3 scénarios positifs possibles", "Concentrez-vous sur le présent"], "duration": "20 min", "material": "Carnet", "why_helps": "Questionner l''anticipation négative développe un rapport plus équilibré au futur."}', '{"anxieux"}', 60, 365, false),
('Playlist détente', 'Création d''un outil d''apaisement musical', 'evening', 'explanatory', '{"objective": "Constituer une ressource d''apaisement rapide", "instructions": ["Sélectionnez 10 morceaux qui vous apaisent instantanément", "Variez les styles : classique, nature, instrumental", "Testez l''effet de chaque morceau", "Organisez par intensité d''apaisement", "Utilisez cette playlist en cas de crise"], "duration": "30 min", "material": "Application musicale", "why_helps": "Une playlist dédiée devient un outil thérapeutique accessible immédiatement."}', '{"anxieux"}', 65, 365, false),
('Coucher plus tôt', 'Améliorer le sommeil pour réduire l''anxiété', 'evening', 'explanatory', '{"objective": "Optimiser la récupération pour une meilleure gestion émotionnelle", "instructions": ["Calculez votre heure de coucher idéale (sommeil + 30 min)", "Préparez votre environnement 1h avant", "Éliminez les stimulants après 18h", "Créez une routine de transition", "Observez l''effet sur votre anxiété"], "duration": "Soirée entière", "material": "Aucun", "why_helps": "Un sommeil suffisant est essentiel pour la régulation émotionnelle et la gestion du stress."}', '{"anxieux"}', 70, 365, false),
('Méditation sécurité', 'Renforcement du sentiment de sécurité intérieure', 'morning', 'audio', '{"objective": "Cultiver un sentiment profond de sécurité", "instructions": ["Asseyez-vous confortablement", "Répétez mentalement ''Je suis en sécurité''", "Visualisez une lumière protectrice autour de vous", "Ressentez cette sécurité dans votre corps", "Ancrez cette sensation pour la journée"], "duration": "10 min", "material": "Aucun", "why_helps": "La répétition d''affirmations sécurisantes reprogramme positivement l''inconscient.", "theme": "safety-meditation"}', '{"anxieux"}', 75, 365, false),
('Dire NON sans culpabilité', 'Affirmation de soi pour réduire le stress', 'afternoon', 'explanatory', '{"objective": "Protéger son énergie en posant des limites", "instructions": ["Identifiez une demande à laquelle vous devriez dire non", "Formulez un refus poli mais ferme", "Entraînez-vous devant un miroir", "Pratiquez sans vous justifier excessivement", "Observez la libération que cela procure"], "duration": "15 min", "material": "Aucun", "why_helps": "Dire non protège des surcharges et réduit l''anxiété liée aux obligations excessives."}', '{"anxieux"}', 80, 365, false),
('Reconnaissance crise montante', 'Développer l''alerte précoce des crises d''anxiété', 'evening', 'notebook', '{"objective": "Anticiper et prévenir les crises d''anxiété", "instructions": ["Rappelez-vous votre dernière montée d''anxiété", "Identifiez les 5 premiers signaux (physiques, mentaux)", "Créez votre ''système d''alerte précoce''", "Définissez 3 actions à faire dès ces signaux", "Testez ce système la prochaine fois"], "duration": "25 min", "material": "Carnet", "why_helps": "Reconnaître les signaux précoces permet d''intervenir avant que l''anxiété ne devienne ingérable."}', '{"anxieux"}', 90, 365, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Nuit sans téléphone', 'Retrouvez un sommeil réparateur en éliminant les écrans', 'evening', 'explanatory', '{"objective": "Améliorer la qualité de votre sommeil", "instructions": ["1h avant le coucher, éteignez tous les écrans", "Placez votre téléphone dans une autre pièce", "Préparez une activité alternative (lecture, méditation)", "Observez vos sensations sans stimulation numérique", "Notez la différence de qualité de sommeil au réveil"], "duration": "Toute la soirée", "material": "Livre ou activité calme", "why_helps": "La lumière bleue perturbe la production de mélatonine. L''éviter améliore significativement le sommeil."}', '{"fatigue"}', 1, 30, false),
('Hydratation matinale', 'Réveillez votre organisme en douceur', 'morning', 'explanatory', '{"objective": "Réactiver votre métabolisme après la nuit", "instructions": ["Dès le réveil, avant toute autre boisson", "Buvez un grand verre d''eau à température ambiante", "Buvez lentement, en pleine conscience", "Ressentez l''eau qui hydrate votre corps", "Attendez 20 minutes avant le petit-déjeuner"], "duration": "5 min", "material": "Un verre d''eau", "why_helps": "Après 6-8h sans hydratation, le corps a besoin d''eau pour relancer tous ses processus métaboliques."}', '{"fatigue"}', 1, 60, false),
('Scan des signes de fatigue', 'Apprenez à reconnaître les signaux de votre corps', 'afternoon', 'notebook', '{"objective": "Développer votre écoute corporelle", "instructions": ["Asseyez-vous confortablement", "Scannez votre corps de la tête aux pieds", "Notez chaque sensation de fatigue", "Identifiez : fatigue physique ou mentale ?", "Listez les signaux d''alerte précoces", "Créez votre ''baromètre personnel'' de fatigue"], "duration": "15 min", "material": "Carnet", "why_helps": "Reconnaître les signaux précoces permet d''agir avant l''épuisement total."}', '{"fatigue"}', 3, 90, false),
('Planning léger', 'Créez une journée respectueuse de votre énergie', 'morning', 'notebook', '{"objective": "Adapter votre agenda à vos capacités réelles", "instructions": ["Listez vos tâches prévues", "Évaluez l''énergie nécessaire pour chacune (1-5)", "Gardez seulement les tâches prioritaires", "Planifiez les tâches lourdes quand vous êtes en forme", "Ajoutez des pauses entre chaque activité", "Prévoyez 30% de marge pour l''imprévu"], "duration": "20 min", "material": "Agenda", "why_helps": "Un planning adapté évite la surcharge et permet une meilleure gestion de l''énergie."}', '{"fatigue"}', 5, 120, false),
('Micro-sieste réparatrice', 'Rechargez vos batteries en 20 minutes', 'afternoon', 'audio', '{"objective": "Récupérer de l''énergie sans perturber le sommeil nocturne", "instructions": ["Trouvez un endroit calme", "Réglez une alarme sur 20 minutes maximum", "Allongez-vous ou asseyez-vous confortablement", "Fermez les yeux même si vous ne dormez pas", "Laissez votre esprit vagabonder", "Réveillez-vous en douceur"], "duration": "20 min", "material": "Réveil, éventuellement masque", "why_helps": "Une sieste courte restaure les fonctions cognitives sans entrer en sommeil profond.", "theme": "power-nap"}', '{"fatigue"}', 7, 150, false),
('Identifier voleurs d''énergie', 'Découvrez ce qui vous épuise vraiment', 'evening', 'notebook', '{"objective": "Identifier les sources de drainage énergétique", "instructions": ["Listez vos activités de la semaine", "Notez votre niveau d''énergie avant/après chacune", "Identifiez les 5 plus gros ''voleurs d''énergie''", "Cherchez des alternatives ou des façons de les modifier", "Planifiez une réduction progressive"], "duration": "25 min", "material": "Carnet", "why_helps": "Identifier les sources de fatigue permet de les gérer proactivement."}', '{"fatigue"}', 10, 180, false),
('Journée sans culpabilité', 'Accordez-vous le droit au repos', 'morning', 'explanatory', '{"objective": "Éliminer la culpabilité liée au repos", "instructions": ["Choisissez une journée pour vous reposer sans culpabilité", "Préparez des activités douces (lecture, bain, sieste)", "Éteignez la voix critique intérieure", "Rappelez-vous : le repos est productif", "Observez comme vous vous sentez mieux après"], "duration": "Journée entière", "material": "Activités douces", "why_helps": "Le repos sans culpabilité permet une récupération profonde et durable."}', '{"fatigue"}', 12, 200, false),
('Lever plus tard', 'Respectez vos besoins de sommeil', 'morning', 'explanatory', '{"objective": "Optimiser votre récupération", "instructions": ["Si possible, levez-vous 30 minutes plus tard", "Préparez la veille pour compenser", "Observez votre niveau d''énergie", "Ajustez selon vos sensations", "Créez un nouveau rythme si bénéfique"], "duration": "Matinée", "material": "Aucun", "why_helps": "Respecter ses besoins de sommeil améliore l''énergie globale de la journée."}', '{"fatigue"}', 14, 220, false),
('Supprimer une tâche', 'Allégez votre charge quotidienne', 'afternoon', 'explanatory', '{"objective": "Réduire la pression et préserver l''énergie", "instructions": ["Regardez votre liste de tâches du jour", "Identifiez une tâche non urgente", "Supprimez-la ou reportez-la", "Observez le soulagement ressenti", "Utilisez ce temps pour vous reposer"], "duration": "5 min", "material": "Liste de tâches", "why_helps": "Alléger sa charge permet de préserver l''énergie pour l''essentiel."}', '{"fatigue"}', 16, 240, false),
('Sieste mentale musicale', 'Repos mental avec accompagnement sonore', 'afternoon', 'audio', '{"objective": "Reposer l''esprit sans dormir", "instructions": ["Allongez-vous confortablement", "Mettez de la musique douce", "Fermez les yeux", "Laissez votre esprit vagabonder", "Ne luttez pas contre les pensées", "Restez 15 minutes minimum"], "duration": "15 min", "material": "Musique douce, lieu confortable", "why_helps": "Le repos mental permet de récupérer sans sommeil profond.", "theme": "mental-rest"}', '{"fatigue"}', 18, 260, false),
('Reprendre une habitude perdue', 'Reconnectez-vous à ce qui vous faisait du bien', 'evening', 'explanatory', '{"objective": "Réactiver une source de bien-être", "instructions": ["Identifiez une habitude positive que vous avez abandonnée", "Comprenez pourquoi elle vous faisait du bien", "Adaptez-la à votre situation actuelle", "Recommencez par une version simplifiée", "Observez l''effet sur votre énergie"], "duration": "Variable", "material": "Selon l''habitude", "why_helps": "Reprendre des habitudes bénéfiques restaure progressivement l''énergie vitale."}', '{"fatigue"}', 20, 280, false),
('Bain/douche consciente', 'Moment de ressourcement par l''eau', 'evening', 'explanatory', '{"objective": "Utiliser l''eau comme thérapie", "instructions": ["Préparez un bain chaud ou une douche", "Concentrez-vous sur les sensations de l''eau", "Respirez profondément la vapeur", "Visualisez l''eau qui emporte votre fatigue", "Prenez le temps nécessaire"], "duration": "20-30 min", "material": "Bain ou douche", "why_helps": "L''eau chaude détend les muscles et favorise la circulation, réduisant la fatigue."}', '{"fatigue"}', 22, 300, false),
('Journal du sommeil', 'Optimisez votre récupération nocturne', 'evening', 'notebook', '{"objective": "Comprendre et améliorer votre sommeil", "instructions": ["Notez votre heure de coucher et de lever", "Évaluez la qualité de votre sommeil (1-10)", "Notez ce que vous avez fait avant de dormir", "Identifiez les facteurs qui améliorent/dégradent votre sommeil", "Ajustez vos habitudes selon vos observations"], "duration": "10 min", "material": "Carnet dédié", "why_helps": "Analyser son sommeil permet d''optimiser cette source principale de récupération."}', '{"fatigue"}', 25, 320, false),
('Bain de soleil', 'Rechargez-vous avec la lumière naturelle', 'morning', 'explanatory', '{"objective": "Réguler vos rythmes circadiens", "instructions": ["Sortez prendre le soleil 5-10 minutes", "Si pas de soleil, utilisez une lampe de luminothérapie", "Exposez votre visage à la lumière", "Respirez l''air frais", "Ressentez l''énergie de la lumière"], "duration": "10 min", "material": "Aucun ou lampe de luminothérapie", "why_helps": "La lumière régule la production de mélatonine et améliore l''énergie diurne."}', '{"fatigue"}', 28, 340, false),
('Petit-déjeuner complet', 'Donnez le carburant nécessaire à votre corps', 'morning', 'explanatory', '{"objective": "Optimiser votre énergie matinale", "instructions": ["Préparez un petit-déjeuner équilibré (protéines, glucides, lipides)", "Mangez assis, calmement", "Mastiquez bien chaque bouchée", "Évitez les écrans pendant le repas", "Observez votre niveau d''énergie après"], "duration": "20 min", "material": "Aliments nutritifs", "why_helps": "Un petit-déjeuner équilibré stabilise la glycémie et fournit l''énergie pour la matinée."}', '{"fatigue"}', 30, 365, false),
('Réduction du bruit', 'Créez un environnement reposant', 'afternoon', 'explanatory', '{"objective": "Diminuer la fatigue liée aux stimulations sonores", "instructions": ["Identifiez les sources de bruit chez vous", "Réduisez ou éliminez les bruits inutiles", "Utilisez des bouchons d''oreilles si nécessaire", "Créez des zones de silence", "Observez l''effet sur votre niveau de fatigue"], "duration": "30 min", "material": "Bouchons d''oreilles éventuels", "why_helps": "La réduction du bruit diminue le stress cognitif et permet une meilleure récupération."}', '{"fatigue"}', 35, 365, false),
