INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Minute d''imprévu', 'Introduire délibérément du chaos contrôlé', 'morning', 'explanatory', '{"objective": "S''habituer à l''imprévisibilité", "instructions": ["Choisissez 1 élément de votre routine matinale", "Changez-le délibérément aujourd''hui", "Observez votre réaction sans la juger", "Notez ce que vous apprenez"], "duration": "5 minutes", "material": "Aucun", "why_helps": "S''exposer graduellement à l''imprévu développe la tolérance à l''incertitude."}', ARRAY['controlant'], 1, 365, false),
