('Méditation de l''eau', 'Apprendre la fluidité par la visualisation', 'afternoon', 'audio', '{"duration": 600, "theme": "water-flow-meditation", "objective": "Développer la fluidité", "instructions": ["Visualisez-vous comme de l''eau", "Vous vous adaptez à tout contenant", "Vous trouvez toujours votre chemin"], "why_helps": "L''eau est un symbole puissant de fluidité et d''adaptation naturelle."}', ARRAY['controlant'], 3, 365, false),
('Expérience sociale spontanée', 'Interaction non planifiée avec autrui', 'morning', 'explanatory', '{"objective": "Pratiquer la spontanéité sociale", "instructions": ["Engagez une conversation non planifiée", "Posez une question ouverte à un inconnu", "Acceptez une invitation impromptue", "Laissez la conversation évoluer naturellement"], "duration": "Variable", "material": "Aucun", "why_helps": "Les interactions spontanées développent la capacité d''adaptation sociale."}', ARRAY['controlant'], 4, 365, false),
('Technique du ''Oui'' automatique', 'Accepter sans analyser', 'afternoon', 'explanatory', '{"objective": "Réduire la sur-analyse", "instructions": ["Pendant 1 heure, dites oui à toute demande raisonnable", "Ne réfléchissez pas, répondez immédiatement", "Observez vos sensations", "Que se passe-t-il quand vous ne contrôlez pas ?"], "duration": "60 minutes", "material": "Aucun", "why_helps": "Cette pratique interrompt les patterns de contrôle et développe la spontanéité."}', ARRAY['controlant'], 5, 365, false),
('Méditation du nuage', 'Observer sans saisir', 'evening', 'audio', '{"duration": 480, "theme": "cloud-watching-meditation", "objective": "Développer le non-attachement", "instructions": ["Observez vos pensées comme des nuages", "Elles passent naturellement", "Vous n''avez pas besoin de les retenir"], "why_helps": "Cette pratique développe le non-attachement et réduit le besoin de contrôle mental."}', ARRAY['controlant'], 6, 365, false),
('Défi de la delegation', 'Confier une tâche importante', 'morning', 'explanatory', '{"objective": "Pratiquer la confiance", "instructions": ["Choisissez une tâche que vous faites habituellement", "Confiez-la à quelqu''un d''autre", "Résistez à l''envie de superviser", "Acceptez que ce soit fait différemment"], "duration": "Variable", "material": "Aucun", "why_helps": "Déléguer développe la confiance et réduit le besoin de tout contrôler."}', ARRAY['controlant'], 7, 365, false),
('Pratique de l''erreur volontaire', 'Faire des erreurs intentionnelles', 'afternoon', 'explanatory', '{"objective": "Désensibiliser à l''imperfection", "instructions": ["Faites délibérément une petite erreur sans conséquence", "Laissez une faute de frappe dans un message amical", "Portez des chaussettes dépareillées", "Observez que le monde ne s''effondre pas"], "duration": "Variable", "material": "Aucun", "why_helps": "S''exposer volontairement aux erreurs réduit la peur de l''imperfection."}', ARRAY['controlant'], 8, 365, false),
('Méditation du lâcher-prise', 'Relâcher physiquement et mentalement', 'evening', 'audio', '{"duration": 720, "theme": "letting-go-meditation", "objective": "Pratiquer le relâchement complet", "instructions": ["Relâchez consciemment chaque tension", "Lâchez prise sur vos attentes", "Laissez être ce qui est"], "why_helps": "Le lâcher-prise physique facilite le lâcher-prise mental et émotionnel."}', ARRAY['controlant'], 9, 365, false),
('Journée sans planning', 'Vivre une journée en totale spontanéité', 'morning', 'explanatory', '{"objective": "Expérimenter la liberté totale", "instructions": ["Ne planifiez rien pour aujourd''hui (si possible)", "Suivez vos envies du moment", "Acceptez les opportunités qui se présentent", "Observez vos résistances"], "duration": "Journée entière", "material": "Aucun", "why_helps": "Une journée sans plan développe la confiance dans sa capacité d''adaptation."}', ARRAY['controlant'], 10, 365, false),
('Technique de respiration chaotique', 'Respiration irrégulière pour lâcher prise', 'afternoon', 'explanatory', '{"objective": "Briser les patterns de contrôle", "instructions": ["Respirez sans rythme régulier", "Variez la longueur et l''intensité", "Laissez votre corps respirer naturellement", "Abandonnez le contrôle de la respiration"], "duration": "10 minutes", "material": "Aucun", "why_helps": "Abandonner le contrôle de la respiration symbolise le lâcher-prise général."}', ARRAY['controlant'], 11, 365, false),
('Journal des imprévus positifs', 'Reconnaître les bienfaits de l''inattendu', 'evening', 'notebook', '{"objective": "Changer la perception de l''imprévu", "prompt": "Rappelez-vous 5 imprévus qui ont finalement été positifs dans votre vie. Qu''ont-ils apporté que vous n''aviez pas prévu ?", "duration": "12 minutes", "material": "Carnet", "why_helps": "Se rappeler les imprévus positifs change l''attitude envers l''incertitude."}', ARRAY['controlant'], 12, 365, false),
('Méditation de la feuille au vent', 'S''abandonner au mouvement naturel', 'morning', 'audio', '{"duration": 540, "theme": "leaf-in-wind-meditation", "objective": "Cultiver l''abandon confiant", "instructions": ["Visualisez-vous comme une feuille", "Le vent vous porte où vous devez aller", "Faites confiance au mouvement"], "why_helps": "Cette métaphore cultive la confiance dans le flux naturel de la vie."}', ARRAY['controlant'], 13, 365, false),
('Exercice du ''Et si ?'' positif', 'Reframing des scénarios catastrophiques', 'afternoon', 'explanatory', '{"objective": "Transformer l''anxiété anticipatoire", "instructions": ["Prenez une situation que vous voulez contrôler", "Au lieu de ''Et si ça tourne mal ?''", "Demandez-vous ''Et si ça tournait mieux que prévu ?''", "Explorez les possibilités positives"], "duration": "8 minutes", "material": "Aucun", "why_helps": "Reframer positivement les scénarios réduit l''anxiété de contrôle."}', ARRAY['controlant'], 14, 365, false),
('Pratique de la confiance aveugle', 'Moments de confiance totale', 'evening', 'explanatory', '{"objective": "Développer la confiance fondamentale", "instructions": ["Choisissez quelqu''un en qui vous avez confiance", "Laissez-le décider pour vous sur quelque chose", "Ne posez pas de questions, suivez", "Observez vos sensations"], "duration": "Variable", "material": "Personne de confiance", "why_helps": "La confiance aveugle temporaire développe la capacité de lâcher prise."}', ARRAY['controlant'], 15, 365, false),
('Méditation de l''océan', 'Se laisser porter par plus grand que soi', 'morning', 'audio', '{"duration": 600, "theme": "ocean-surrender-meditation", "objective": "Cultiver l''abandon confiant", "instructions": ["Visualisez-vous flottant dans l''océan", "L''océan vous porte naturellement", "Vous n''avez rien à faire, juste être"], "why_helps": "L''océan symbolise la force bienveillante qui nous porte quand on lâche prise."}', ARRAY['controlant'], 16, 365, false),
('Technique du chaos créatif', 'Créer dans le désordre', 'afternoon', 'explanatory', '{"objective": "Découvrir la créativité du chaos", "instructions": ["Mélangez des éléments au hasard", "Couleurs, mots, objets", "Créez quelque chose avec ce mélange", "Laissez émerger sans planifier"], "duration": "20 minutes", "material": "Matériel créatif varié", "why_helps": "Le chaos créatif révèle que le désordre peut générer de la beauté."}', ARRAY['controlant'], 17, 365, false),
('Journal des apprentissages d''imperfection', 'Valoriser les leçons des erreurs', 'evening', 'notebook', '{"objective": "Transformer la relation à l''erreur", "prompt": "Listez 3 erreurs récentes. Pour chacune : Qu''ai-je appris ? En quoi m''a-t-elle fait grandir ? Comment puis-je être reconnaissant pour cette leçon ?", "duration": "15 minutes", "material": "Carnet", "why_helps": "Voir les erreurs comme des apprentissages réduit la peur de l''imperfection."}', ARRAY['controlant'], 18, 365, false),
('Méditation de la graine', 'Faire confiance au processus naturel', 'morning', 'audio', '{"duration": 480, "theme": "seed-trust-meditation", "objective": "Cultiver la patience et la confiance", "instructions": ["Vous êtes une graine dans la terre", "Vous faites confiance au processus", "Votre croissance se fait naturellement"], "why_helps": "La métaphore de la graine enseigne la patience et la confiance dans les processus naturels."}', ARRAY['controlant'], 19, 365, false),
('Défi de communication ouverte', 'Exprimer sans chercher à convaincre', 'afternoon', 'explanatory', '{"objective": "Communiquer sans contrôler", "instructions": ["Exprimez votre opinion sans chercher à convaincre", "Écoutez vraiment la réponse", "Acceptez les désaccords", "Laissez l''autre libre de ses choix"], "duration": "Variable", "material": "Aucun", "why_helps": "Communiquer sans chercher à contrôler développe des relations plus authentiques."}', ARRAY['controlant'], 20, 365, false),
('Rituel de remise', 'Confier symboliquement ses préoccupations', 'evening', 'explanatory', '{"objective": "Relâcher symboliquement le contrôle", "instructions": ["Écrivez vos préoccupations sur papier", "Brûlez le papier en sécurité", "Ou plantez-le au pied d''un arbre", "Confiez vos soucis à plus grand que vous"], "duration": "15 minutes", "material": "Papier, feu ou terre", "why_helps": "Les rituels de remise symbolisent et facilitent le lâcher-prise concret."}', ARRAY['controlant'], 21, 365, false),
('Méditation de l''oiseau en vol', 'Liberté et fluidité du mouvement', 'morning', 'audio', '{"duration": 540, "theme": "bird-flight-meditation", "objective": "Ressentir la liberté", "instructions": ["Visualisez-vous volant comme un oiseau", "Libre de toute contrainte", "Vous suivez les courants naturels"], "why_helps": "Cette visualisation connecte à la sensation de liberté et de fluidité naturelle."}', ARRAY['controlant'], 22, 365, false),
('Pratique de l''accueil total', 'Accepter tout ce qui vient', 'afternoon', 'explanatory', '{"objective": "Développer l''acceptation radicale", "instructions": ["Pour les 2 prochaines heures", "Accueillez tout ce qui se présente", "Dites ''Bienvenue'' à chaque situation", "Même aux difficultés"], "duration": "2 heures", "material": "Aucun", "why_helps": "L''accueil total transforme la résistance en flow et réduit la souffrance."}', ARRAY['controlant'], 23, 365, false),
('Méditation du bambou', 'Flexibilité et résistance', 'evening', 'audio', '{"duration": 600, "theme": "bamboo-flexibility-meditation", "objective": "Cultiver la flexibilité forte", "instructions": ["Comme le bambou, vous êtes flexible", "Vous pliez sans vous casser", "Votre force vient de votre souplesse"], "why_helps": "Le bambou enseigne qu''on peut être fort tout en étant flexible."}', ARRAY['controlant'], 24, 365, false),
('Bilan de lâcher-prise', 'Évaluer ses progrès dans l''abandon du contrôle', 'morning', 'notebook', '{"objective": "Mesurer l''évolution du lâcher-prise", "prompt": "Ce mois-ci : Dans quels domaines avez-vous lâché prise ? Quels ont été les résultats ? Qu''avez-vous découvert sur vous-même ?", "duration": "20 minutes", "material": "Carnet", "why_helps": "Mesurer ses progrès encourage la poursuite du lâcher-prise et renforce les apprentissages."}', ARRAY['controlant'], 25, 365, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
