INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Journal libre express', 'Écriture sans censure pour libérer l''expression', 'morning', 'notebook', '{"objective": "Débloquer l''expression naturelle", "prompt": "Écrivez pendant 10 minutes sans vous arrêter, sans censure, sans relire. Laissez sortir tout ce qui vient. Si vous bloquez, écrivez ''je ne sais pas quoi écrire'' jusqu''à ce que ça revienne.", "duration": "10 minutes", "material": "Carnet privé", "why_helps": "L''écriture libre court-circuite les mécanismes de refoulement et libère l''expression authentique."}', ARRAY['refoule'], 1, 365, false),
('Méditation des besoins cachés', 'Reconnecter avec ses besoins profonds', 'afternoon', 'audio', '{"duration": 540, "theme": "hidden-needs-meditation", "objective": "Redécouvrir ses besoins authentiques", "instructions": ["Explorez vos besoins profonds", "Ceux que vous n''osez pas exprimer", "Ils ont le droit d''exister"], "why_helps": "Cette méditation aide à reconnecter avec les besoins refoulés par conditionnement social."}', ARRAY['refoule'], 2, 365, false),
('Technique du dialogue intérieur', 'Conversation avec sa voix intérieure', 'evening', 'explanatory', '{"objective": "Renouer le dialogue interne", "instructions": ["Posez-vous la question : Qu''est-ce que j''aimerais vraiment dire ?", "Écoutez la réponse sans la juger", "Poursuivez la conversation", "Acceptez ce qui émerge"], "duration": "15 minutes", "material": "Aucun", "why_helps": "Le dialogue intérieur rétablit la communication avec les parties refoulées de soi."}', ARRAY['refoule'], 3, 365, false),
('Expression corporelle libre', 'Laisser le corps s''exprimer naturellement', 'morning', 'explanatory', '{"objective": "Libérer l''expression corporelle", "instructions": ["Mettez une musique qui vous touche", "Bougez comme votre corps en a envie", "Sans chorégraphie, juste impulses", "Laissez sortir ce qui est bloqué"], "duration": "15 minutes", "material": "Musique, espace privé", "why_helps": "Le corps garde les émotions refoulées, le mouvement libre les libère."}', ARRAY['refoule'], 4, 365, false),
('Méditation de l''enfant intérieur', 'Reconnecter avec son authenticité originelle', 'afternoon', 'audio', '{"duration": 600, "theme": "inner-child-reconnection", "objective": "Retrouver son authenticité", "instructions": ["Rencontrez votre enfant intérieur", "Écoutez ce qu''il veut vous dire", "Il n''a jamais cessé d''exister"], "why_helps": "L''enfant intérieur détient notre authenticité d''avant le refoulement social."}', ARRAY['refoule'], 5, 365, false),
('Technique du miroir émotionnel', 'Reconnaître ses émotions par l''observation', 'evening', 'explanatory', '{"objective": "Reconnecter avec ses émotions", "instructions": ["Regardez-vous dans un miroir", "Observez votre expression", "Que dit votre visage ?", "Quelles émotions y lisez-vous ?"], "duration": "10 minutes", "material": "Miroir", "why_helps": "Le visage exprime souvent les émotions que nous refoulons consciemment."}', ARRAY['refoule'], 6, 365, false),
('Journal des non-dits', 'Exprimer ce qui n''a jamais été dit', 'morning', 'notebook', '{"objective": "Libérer la parole retenue", "prompt": "Écrivez une lettre à quelqu''un à qui vous n''avez jamais dit ce que vous pensiez vraiment. Vous n''êtes pas obligé de l''envoyer. Libérez juste votre vérité.", "duration": "20 minutes", "material": "Carnet", "why_helps": "Exprimer les non-dits, même sur papier, libère la tension du refoulement."}', ARRAY['refoule'], 7, 365, false),
('Méditation des émotions interdites', 'Accueillir les émotions ''inacceptables''', 'afternoon', 'audio', '{"duration": 480, "theme": "forbidden-emotions-meditation", "objective": "Légitimer toutes ses émotions", "instructions": ["Pensez à une émotion que vous vous interdisez", "Colère, tristesse, jalousie...", "Elle a le droit d''exister"], "why_helps": "Légitimer les émotions ''interdites'' réduit le refoulement et l''auto-censure."}', ARRAY['refoule'], 8, 365, false),
('Technique de l''affirmation progressive', 'Développer l''assertivité par étapes', 'evening', 'explanatory', '{"objective": "Développer l''expression de soi", "instructions": ["Commencez par exprimer des préférences mineures", "''Je préfère ce restaurant''", "Progressez vers des opinions plus personnelles", "Exercez-vous quotidiennement"], "duration": "Variable", "material": "Aucun", "why_helps": "L''assertivité progressive réhabilite le droit à l''expression personnelle."}', ARRAY['refoule'], 9, 365, false),
('Exploration des masques sociaux', 'Identifier ses personas et retrouver son authenticité', 'morning', 'notebook', '{"objective": "Distinguer masques et authenticité", "prompt": "Listez vos différents ''masques'' sociaux : au travail, en famille, avec les amis. Qui êtes-vous vraiment sous ces masques ? Qu''aimeriez-vous exprimer de plus authentique ?", "duration": "18 minutes", "material": "Carnet", "why_helps": "Prendre conscience des masques permet de choisir plus consciemment quand être authentique."}', ARRAY['refoule'], 10, 365, false),
('Méditation de l''authenticité', 'Reconnecter avec son essence véritable', 'afternoon', 'audio', '{"duration": 540, "theme": "authentic-self-meditation", "objective": "Retrouver son moi authentique", "instructions": ["Sous tous vos rôles et masques", "Il y a votre essence authentique", "Elle n''a jamais disparu"], "why_helps": "Cette méditation aide à retrouver et honorer son moi authentique sous les couches sociales."}', ARRAY['refoule'], 11, 365, false),
('Technique du ''oui'' et du ''non''', 'Réapprendre à poser ses limites', 'evening', 'explanatory', '{"objective": "Retrouver son pouvoir de choix", "instructions": ["Entraînez-vous à dire non à de petites demandes", "Et oui à ce qui vous fait vraiment plaisir", "Observez vos résistances", "Votre oui et votre non ont de la valeur"], "duration": "Variable", "material": "Aucun", "why_helps": "Réapprendre à dire non et oui consciemment restaure le pouvoir personnel."}', ARRAY['refoule'], 12, 365, false),
('Journal des désirs secrets', 'Explorer ses désirs authentiques', 'morning', 'notebook', '{"objective": "Reconnecter avec ses désirs vrais", "prompt": "Si personne ne pouvait vous juger, que désireriez-vous vraiment ? Explorez vos désirs les plus profonds, même ceux qui vous semblent interdits ou impossibles.", "duration": "15 minutes", "material": "Carnet privé", "why_helps": "Reconnaître ses désirs authentiques est la première étape pour les honorer."}', ARRAY['refoule'], 13, 365, false),
('Méditation de libération vocale', 'Retrouver sa voix authentique', 'afternoon', 'audio', '{"duration": 420, "theme": "voice-liberation-meditation", "objective": "Libérer sa voix intérieure", "instructions": ["Votre voix mérite d''être entendue", "Elle porte votre vérité unique", "Donnez-lui la permission de s''exprimer"], "why_helps": "Cette méditation aide à surmonter la peur de s''exprimer et valorise la voix unique."}', ARRAY['refoule'], 14, 365, false),
('Technique de l''expression créative', 'Canaliser ses émotions par l''art', 'evening', 'explanatory', '{"objective": "Exprimer par la créativité", "instructions": ["Choisissez un médium créatif", "Dessin, chant, danse, écriture", "Exprimez une émotion que vous retenez", "Laissez-la s''exprimer à travers l''art"], "duration": "20 minutes", "material": "Matériel créatif", "why_helps": "L''art permet d''exprimer ce que les mots ne peuvent pas toujours dire."}', ARRAY['refoule'], 15, 365, false),
('Dialogue avec ses peurs', 'Comprendre ce qui bloque l''expression', 'morning', 'notebook', '{"objective": "Identifier les freins à l''expression", "prompt": "Qu''est-ce qui vous empêche de vous exprimer authentiquement ? Peur du jugement ? Du rejet ? Du conflit ? Dialoguez avec ces peurs sur papier.", "duration": "12 minutes", "material": "Carnet", "why_helps": "Comprendre ses peurs permet de les apprivoiser et de les dépasser progressivement."}', ARRAY['refoule'], 16, 365, false),
('Méditation de l''océan intérieur', 'Explorer ses profondeurs émotionnelles', 'afternoon', 'audio', '{"duration": 600, "theme": "inner-ocean-meditation", "objective": "Explorer ses profondeurs", "instructions": ["Plongez dans votre océan intérieur", "Explorez vos profondeurs émotionnelles", "Tout ce qui y réside a sa place"], "why_helps": "Cette métaphore encourage l''exploration de toutes les facettes de soi."}', ARRAY['refoule'], 17, 365, false),
('Pratique de l''écoute intérieure', 'Développer l''attention à ses signaux internes', 'evening', 'explanatory', '{"objective": "Reconnecter avec ses signaux internes", "instructions": ["Plusieurs fois par jour, demandez-vous", "Comment je me sens maintenant ?", "Qu''est-ce que mon corps me dit ?", "Qu''est-ce que mon cœur me dit ?"], "duration": "2 minutes x plusieurs fois", "material": "Aucun", "why_helps": "L''écoute intérieure régulière rétablit la connexion avec ses ressentis authentiques."}', ARRAY['refoule'], 18, 365, false),
('Journal de réconciliation', 'Se réconcilier avec ses parts rejetées', 'morning', 'notebook', '{"objective": "Intégrer ses aspects refoulés", "prompt": "Identifiez une partie de vous que vous avez longtemps rejetée. Écrivez-lui une lettre de réconciliation. Comment pouvez-vous l''accueillir avec bienveillance ?", "duration": "20 minutes", "material": "Carnet", "why_helps": "La réconciliation avec ses parts refoulées permet une intégration harmonieuse de sa personnalité."}', ARRAY['refoule'], 19, 365, false),
('Méditation de l''arbre généalogique émotionnel', 'Comprendre l''héritage familial du refoulement', 'afternoon', 'audio', '{"duration": 540, "theme": "emotional-family-tree", "objective": "Comprendre les patterns familiaux", "instructions": ["Explorez les patterns émotionnels familiaux", "Qu''aviez-vous le droit d''exprimer ?", "Vous pouvez choisir différemment"], "why_helps": "Comprendre l''héritage familial permet de choisir consciemment ses propres patterns d''expression."}', ARRAY['refoule'], 20, 365, false),
('Technique de l''affirmation de soi quotidienne', 'Pratique progressive de l''authenticité', 'evening', 'explanatory', '{"objective": "Intégrer l''authenticité au quotidien", "instructions": ["Chaque jour, exprimez une vérité sur vous", "Commencez petit : ''J''aime cette couleur''", "Progressez : ''Je ne suis pas d''accord''", "Célébrez chaque expression authentique"], "duration": "Variable", "material": "Aucun", "why_helps": "La pratique quotidienne de l''authenticité reconstruit progressivement la confiance en son expression."}', ARRAY['refoule'], 21, 365, false),
('Méditation de la source', 'Reconnecter avec son essence originelle', 'morning', 'audio', '{"duration": 480, "theme": "original-essence-meditation", "objective": "Retrouver son essence pure", "instructions": ["Remontez à votre source originelle", "Avant le conditionnement social", "Cette essence pure existe toujours"], "why_helps": "Cette méditation aide à retrouver l''authenticité d''avant les conditionnements sociaux."}', ARRAY['refoule'], 22, 365, false),
('Pratique de transparence progressive', 'Oser montrer qui on est vraiment', 'afternoon', 'explanatory', '{"objective": "Développer l''authenticité relationnelle", "instructions": ["Choisissez une personne de confiance", "Partagez quelque chose de plus personnel", "Observez que l''authenticité crée plus de connexion", "Progressez avec d''autres personnes"], "duration": "Variable", "material": "Aucun", "why_helps": "La transparence progressive prouve que l''authenticité améliore les relations."}', ARRAY['refoule'], 23, 365, false),
('Rituel de libération symbolique', 'Cérémonie pour libérer le refoulement', 'evening', 'explanatory', '{"objective": "Marquer symboliquement sa libération", "instructions": ["Écrivez sur papier ce que vous vous interdisiez d''être", "Brûlez le papier en sécurité", "Déclarez : ''Je me donne le droit d''être moi''", "Célébrez cette nouvelle liberté"], "duration": "20 minutes", "material": "Papier, feu sécurisé", "why_helps": "Les rituels symboliques marquent puissamment les transformations intérieures."}', ARRAY['refoule'], 24, 365, false),
('Bilan d''authenticité', 'Évaluer son chemin vers l''expression authentique', 'morning', 'notebook', '{"objective": "Mesurer ses progrès d''authenticité", "prompt": "Ces derniers mois : Dans quels domaines vous exprimez-vous plus authentiquement ? Qu''avez-vous découvert sur vous ? Quels sont vos prochains pas vers plus d''authenticité ?", "duration": "25 minutes", "material": "Carnet", "why_helps": "Mesurer ses progrès d''authenticité encourage la poursuite du développement personnel."}', ARRAY['refoule'], 25, 365, false);
INSERT INTO activities (title, description, type, activity_format, content, target_profiles, day_min, day_max, is_premium) VALUES
('Défouloir matinal', 'Évacuer la pression avant qu''elle n''explose', 'morning', 'explanatory', '{"objective": "Prévenir l''accumulation de tension", "instructions": ["Secouez énergiquement tout votre corps 2 minutes", "Frappez dans un coussin 1 minute", "Criez dans une serviette si nécessaire", "Respirez profondément pour revenir au calme"], "duration": "5 minutes", "material": "Coussin, serviette", "why_helps": "Évacuer régulièrement la tension empêche l''accumulation qui mène aux explosions."}', ARRAY['volcan'], 1, 365, false),
('Journal de la colère', 'Exprimer sa colère par l''écriture', 'evening', 'notebook', '{"objective": "Canaliser la colère constructivement", "prompt": "Qu''est-ce qui vous a mis en colère aujourd''hui ? Écrivez sans retenue, utilisez les mots que vous voulez. Laissez sortir toute la pression. Puis réfléchissez : que me dit cette colère ?", "duration": "15 minutes", "material": "Carnet privé", "why_helps": "L''écriture permet d''exprimer la colère sans faire de dégâts relationnels."}', ARRAY['volcan'], 2, 365, false),
('Méditation de la transformation du feu', 'Transformer la colère en énergie positive', 'afternoon', 'audio', '{"duration": 540, "theme": "fire-transformation-meditation", "objective": "Canaliser l''énergie de la colère", "instructions": ["Votre colère est un feu puissant", "Transformez ce feu en énergie créatrice", "Utilisez cette force pour du positif"], "why_helps": "Cette méditation aide à voir la colère comme une énergie transformable plutôt que destructrice."}', ARRAY['volcan'], 3, 365, false),
('Technique de la pause salvratrice', 'Interrompre l''escalade avant l''explosion', 'morning', 'explanatory', '{"objective": "Prévenir les explosions relationnelles", "instructions": ["Dès que vous sentez monter la pression", "Dites : ''J''ai besoin d''une pause''", "Sortez de la situation 10 minutes minimum", "Respirez, marchez, évacuez", "Revenez quand vous êtes plus calme"], "duration": "Variable", "material": "Aucun", "why_helps": "La pause interrompt l''escalade émotionnelle et prévient les dommages relationnels."}', ARRAY['volcan'], 4, 365, false),
('Exercice de défoulement physique', 'Libérer l''énergie explosive par le corps', 'afternoon', 'explanatory', '{"objective": "Évacuer physiquement la tension", "instructions": ["Course intensive 15 minutes", "Ou boxe dans le vide", "Ou travail physique intense", "Jusqu''à sentir la tension diminuer"], "duration": "15-30 minutes", "material": "Espace pour bouger", "why_helps": "L''exercice intense permet de métaboliser les hormones de stress et de colère."}', ARRAY['volcan'], 5, 365, false),
('Méditation de la montagne imperturbable', 'Développer la stabilité intérieure', 'evening', 'audio', '{"duration": 600, "theme": "unshakeable-mountain-meditation", "objective": "Cultiver la stabilité émotionnelle", "instructions": ["Vous êtes une montagne solide", "Les orages émotionnels vous traversent", "Mais vous restez stable et imperturbable"], "why_helps": "Cette visualisation développe un centre de calme face aux turbulences émotionnelles."}', ARRAY['volcan'], 6, 365, false),
('Cartographie des déclencheurs', 'Identifier ce qui allume la mèche', 'morning', 'notebook', '{"objective": "Prévenir les explosions par la connaissance", "prompt": "Listez vos 10 principaux déclencheurs de colère : situations, mots, comportements. Pour chacun, notez le niveau de risque et une stratégie de prévention.", "duration": "20 minutes", "material": "Carnet", "why_helps": "Connaître ses déclencheurs permet de les anticiper et de s''en protéger."}', ARRAY['volcan'], 7, 365, false),
('Technique de respiration refroidissante', 'Calmer le feu intérieur par la respiration', 'afternoon', 'explanatory', '{"objective": "Refroidir l''émotion intense", "instructions": ["Inspirez par le nez lentement", "Expirez par la bouche plus longtemps", "Visualisez l''air frais qui apaise", "Continuez jusqu''à sentir le calme"], "duration": "5-10 minutes", "material": "Aucun", "why_helps": "Cette respiration active le système nerveux parasympathique et calme l''agitation."}', ARRAY['volcan'], 8, 365, false),
('Méditation de l''eau qui éteint le feu', 'Équilibrer le feu par l''élément eau', 'evening', 'audio', '{"duration": 480, "theme": "water-cooling-fire-meditation", "objective": "Trouver l''équilibre des éléments", "instructions": ["Visualisez une eau fraîche et apaisante", "Elle refroidit doucement votre feu intérieur", "Sans l''éteindre, juste l''équilibrer"], "why_helps": "Cette méditation utilise l''équilibre des éléments pour réguler l''intensité émotionnelle."}', ARRAY['volcan'], 9, 365, false),
('Pratique de l''expression constructive', 'Dire sa colère sans détruire', 'morning', 'explanatory', '{"objective": "Communiquer sa colère sainement", "instructions": ["Utilisez des ''Je'' : ''Je me sens en colère quand...''", "Exprimez le besoin derrière la colère", "''J''ai besoin de respect/compréhension/espace''", "Évitez les accusations et généralisations"], "duration": "Variable", "material": "Aucun", "why_helps": "L''expression constructive permet de communiquer sa colère sans endommager les relations."}', ARRAY['volcan'], 10, 365, false),
('Journal des solutions', 'Transformer la colère en action positive', 'afternoon', 'notebook', '{"objective": "Canaliser l''énergie vers des solutions", "prompt": "Face à votre dernière grosse colère : Que vous dit-elle sur vos besoins non satisfaits ? Quelles actions constructives pourriez-vous entreprendre ? Comment transformer cette énergie en force positive ?", "duration": "18 minutes", "material": "Carnet", "why_helps": "Transformer la colère en action constructive lui donne un sens et une utilité."}', ARRAY['volcan'], 11, 365, false),
('Méditation de l''alchimiste', 'Transformer les émotions lourdes en or', 'evening', 'audio', '{"duration": 540, "theme": "emotional-alchemy-meditation", "objective": "Transmuter ses émotions", "instructions": ["Comme un alchimiste transforme les métaux", "Vous transformez votre colère en sagesse", "Votre rage en compassion"], "why_helps": "Cette métaphore aide à voir les émotions difficiles comme des matières premières à transformer."}', ARRAY['volcan'], 12, 365, false),
('Technique du sas de décompression', 'Créer un espace de transition émotionnelle', 'morning', 'explanatory', '{"objective": "Gérer les transitions émotionnelles", "instructions": ["Après une situation stressante", "Accordez-vous 5 minutes de ''sas''", "Respirez, bougez, évacuez", "Avant de passer à autre chose"], "duration": "5 minutes", "material": "Aucun", "why_helps": "Le sas de décompression évite le transfert de tension d''une situation à l''autre."}', ARRAY['volcan'], 13, 365, false),
('Pratique de la colère sacrée', 'Honorer le message de la colère', 'afternoon', 'explanatory', '{"objective": "Comprendre la fonction de la colère", "instructions": ["Votre colère est un gardien", "Elle protège vos valeurs et limites", "Écoutez son message avant de l''évacuer", "Que défend-elle en vous ?"], "duration": "10 minutes", "material": "Aucun", "why_helps": "Voir la colère comme une protection permet de la respecter tout en la gérant."}', ARRAY['volcan'], 14, 365, false),
('Méditation du forgeron', 'Forger sa force dans le feu des émotions', 'evening', 'audio', '{"duration": 600, "theme": "blacksmith-meditation", "objective": "Forger sa force intérieure", "instructions": ["Comme le forgeron utilise le feu", "Vos émotions intenses forgent votre force", "Chaque épreuve vous rend plus solide"], "why_helps": "Cette métaphore transforme les difficultés émotionnelles en opportunités de croissance."}', ARRAY['volcan'], 15, 365, false),
('Journal des victoires sur la colère', 'Célébrer ses progrès de maîtrise', 'morning', 'notebook', '{"objective": "Renforcer les comportements positifs", "prompt": "Cette semaine : Dans quelles situations avez-vous mieux géré votre colère ? Comment avez-vous fait ? Qu''avez-vous appris ? Célébrez ces victoires !", "duration": "12 minutes", "material": "Carnet", "why_helps": "Célébrer ses progrès renforce la motivation et la confiance en sa capacité de changement."}', ARRAY['volcan'], 16, 365, false),
('Technique de l''ancrage au sol', 'Évacuer l''énergie excessive par la terre', 'afternoon', 'explanatory', '{"objective": "Libérer l''excès d''énergie", "instructions": ["Pieds nus sur le sol (terre, herbe)", "Visualisez votre colère qui descend", "Elle s''évacue par vos pieds dans la terre", "La terre absorbe et transforme cette énergie"], "duration": "10 minutes", "material": "Contact avec la terre", "why_helps": "L''ancrage permet d''évacuer l''excès d''énergie émotionnelle de façon naturelle."}', ARRAY['volcan'], 17, 365, false),
('Méditation du phénix', 'Renaissance après les tempêtes émotionnelles', 'evening', 'audio', '{"duration": 540, "theme": "phoenix-rebirth-meditation", "objective": "Se renouveler après les crises", "instructions": ["Comme le phénix renaît de ses cendres", "Vous renaissez de vos tempêtes", "Plus sage et plus fort"], "why_helps": "Cette métaphore aide à voir les crises émotionnelles comme des opportunités de renaissance."}', ARRAY['volcan'], 18, 365, false),
('Pratique de l''humour défouloir', 'Utiliser l''humour pour désamorcer', 'morning', 'explanatory', '{"objective": "Désamorcer par l''humour", "instructions": ["Face à une situation énervante", "Trouvez l''aspect absurde ou comique", "Exagérez mentalement la situation", "Riez de l''absurdité humaine"], "duration": "Variable", "material": "Aucun", "why_helps": "L''humour change la perspective et désamorce l''intensité émotionnelle."}', ARRAY['volcan'], 19, 365, false),
('Création d''un plan d''urgence colère', 'Stratégie personnalisée pour les crises', 'afternoon', 'notebook', '{"objective": "Préparer les moments de crise", "prompt": "Créez votre kit de survie colère : 5 techniques qui marchent pour vous, 3 lieux où vous calmer, 2 personnes à contacter, 1 mantra personnel. Gardez ce plan accessible.", "duration": "20 minutes", "material": "Carnet", "why_helps": "Avoir un plan préparé réduit la panique et guide vers des réactions constructives."}', ARRAY['volcan'], 20, 365, false),
('Méditation de la tempête qui passe', 'Accepter la temporalité des émotions intenses', 'evening', 'audio', '{"duration": 480, "theme": "passing-storm-meditation", "objective": "Comprendre l''impermanence émotionnelle", "instructions": ["Vos colères sont comme des tempêtes", "Intenses mais temporaires", "Elles passent toujours"], "why_helps": "Comprendre que les émotions passent aide à les supporter sans s''identifier à elles."}', ARRAY['volcan'], 21, 365, false),
('Technique de réparation relationnelle', 'Réparer après une explosion', 'morning', 'explanatory', '{"objective": "Restaurer les liens abîmés", "instructions": ["Après une explosion : reconnaissez vos torts", "Excusez-vous sincèrement", "Exprimez ce que vous auriez dû dire", "Demandez comment réparer"], "duration": "Variable", "material": "Aucun", "why_helps": "La réparation immédiate limite les dégâts relationnels et maintient la confiance."}', ARRAY['volcan'], 22, 365, false),
('Rituel de purification par le feu', 'Transformer symboliquement sa colère', 'afternoon', 'explanatory', '{"objective": "Transformer rituellement la colère", "instructions": ["Écrivez votre colère sur papier", "Brûlez-le en sécurité", "Visualisez la transformation en lumière", "Cette énergie sert maintenant du positif"], "duration": "15 minutes", "material": "Papier, feu sécurisé", "why_helps": "Les rituels de transformation donnent un sens et une direction à l''énergie émotionnelle."}', ARRAY['volcan'], 23, 365, false),
('Méditation de l''arbre dans la tempête', 'Flexibilité et enracinement face aux épreuves', 'evening', 'audio', '{"duration": 600, "theme": "tree-in-storm-meditation", "objective": "Développer flexibilité et stabilité", "instructions":["Comme l''arbre qui plie sans rompre", "Vous vous adaptez aux tempêtes", "Vos racines restent solides"], "why_helps": "Cette métaphore enseigne comment rester fort tout en étant flexible face aux défis."}', ARRAY['volcan'], 24, 365, false),
('Bilan de maîtrise émotionnelle', 'Évaluer ses progrès de gestion de colère', 'morning', 'notebook', '{"objective": "Mesurer l''évolution de sa maîtrise", "prompt": "Ces derniers mois : Comment votre relation à la colère a-t-elle évolué ? Quelles stratégies fonctionnent le mieux ? Quels sont vos prochains objectifs de maîtrise ?", "duration": "25 minutes", "material": "Carnet", "why_helps": "L''évaluation régulière encourage les progrès et ajuste les stratégies de gestion émotionnelle."}', ARRAY['volcan'], 25, 365, false);
